using System;
using System.Globalization;
using System.Net;
using System.Net.Http;
using System.Threading.Tasks;
using System.Web.Http.Controllers;
using Microsoft.AspNet.WebHooks.Properties;
using Newtonsoft.Json.Linq;

namespace Microsoft.AspNet.WebHooks
{
    /// <summary>
    /// Provides an <see cref="IWebHookReceiver"/> implementation which supports WebHooks generated by MyGet.
    /// A sample WebHook URI is '<c>https://&lt;host&gt;/api/webhooks/incoming/myget/{id}?code=83699ec7c1d794c0c780e49a5c72972590571fd8</c>'.
    /// For security reasons the WebHook URI must be an <c>https</c> URI and contain a 'code' query parameter with the
    /// same value as configured in the '<c>MS_WebHookReceiverSecret_MyGet</c>' application setting.
    /// The 'code' parameter must be between 32 and 128 characters long.
    /// For details about MyGet WebHooks, see <c>https://docs.myget.org/docs/reference/webhooks</c>.
    /// </summary>
    public class MyGetWebHookReceiver : WebHookReceiver
    {
        internal const string RecName = "myget";

        internal const string MediaType = "application/vnd.myget.webhooks.v1+json";
        internal const string PayloadTypePropertyName = "PayloadType";
        internal const string PingPayload = "PingWebHookEventPayloadV1";

        /// <summary>
        /// Gets the receiver name for this receiver.
        /// </summary>
        public static string ReceiverName
        {
            get { return RecName; }
        }

        /// <inheritdoc />
        public override string Name
        {
            get { return RecName; }
        }

        /// <inheritdoc />
        public override async Task<HttpResponseMessage> ReceiveAsync(string id, HttpRequestContext context, HttpRequestMessage request)
        {
            if (id == null)
            {
                throw new ArgumentNullException(nameof(id));
            }
            if (context == null)
            {
                throw new ArgumentNullException(nameof(context));
            }
            if (request == null)
            {
                throw new ArgumentNullException(nameof(request));
            }

            if (request.Method == HttpMethod.Post)
            {
                // Ensure that we use https and have a valid code parameter
                await EnsureValidCode(request, id);

                // Read the request entity body.
                var data = await ReadAsJsonAsync(request);

                // Read the action from data
                string actionAsString;
                if (!data.TryGetValue(PayloadTypePropertyName, out var action))
                {
                    var message = string.Format(CultureInfo.CurrentCulture, MyGetReceiverResources.Receiver_NoEvent, PayloadTypePropertyName);
                    context.Configuration.DependencyResolver.GetLogger().Error(message);
                    var noPayloadType = request.CreateErrorResponse(HttpStatusCode.BadRequest, message);
                    return noPayloadType;
                }
                else
                {
                    actionAsString = action.Value<string>();
                }

                // If this is a ping request then just return. Otherwise call handlers.
                if (string.Equals(actionAsString, PingPayload, StringComparison.OrdinalIgnoreCase))
                {
                    context.Configuration.DependencyResolver.GetLogger().Info(MyGetReceiverResources.Receiver_PingEvent);
                    return request.CreateResponse();
                }
                return await ExecuteWebHookAsync(id, context, request, new[] { actionAsString }, data);
            }

            return CreateBadMethodResponse(request);
        }
    }
}
